#!/bin/bash
open_queue () { echo Starting Printer Job Queue..;page='';while [[ $page == '' ]]; do var="hello bye Click Here";no=$( lpq -a | wc -l );no=$(( no-1 ));ass=$(lpq -a | tail -n$no | awk -F " " '{ print $3"\n"$2"\n"$1"\n"$4}' |  zenity --list --column="Job ID" --column="User" --column="Queue" --column=File  --width=400 --height=700 --text="<b>select Job id</b>.....\nPress OK to <b>KILL</b>\nPress ... to Open Printer URIs" --title="Printer Job Queue" --cancel-label="..." 2>/dev/null );page=$(echo $ass | awk -F "\n" '{print $1}');echo $ass;if [[ $page == '' ]];then page='x';troubleshoot_device ;else lprm $page;fi;done;} ;troubleshoot_device () { brand=$(zenity --entry --entry-text=HP --text="Enter brand name\n For Eg. HP for hewlett packward.\n type lpinfo -m in terminal to get list of models as stored by Linux Os" );uri_list=$(lpinfo -l -v | grep -i -B 4 -C 2 "make-and-model = "$brand | grep "uri =" | awk -F "uri = " '{print $2 }' | zenity --text="Listing Printer Uri\nCopy to clipboard" --height=400 --width=400 --title="Printer ID" --list --column=DeviceURI 2>/dev/null);echo $uri_list | xclip -selection clipboard ;} ;open_queue; while  zenity --question --text="Refresh printer list?" --width=70;do open_queue;done
#please change make-and-model code to list other printers as well (C)puneet bapna Thank YOU!
#please remove >/dev/nul after zenity if you want to view output of zenity command
#zenity --entry-text=HP is the default built in entry of Printer model name change it for ease of us
